# Add 5' and 3' bp seqs to the THF/THF tectoRNAsimport numpy as npimport pandas as pd###read in tertiary contact sequencesdf = pd.read_csv("fngrprt_300_orig_GGAA_11ntR.csv")print(df.head())###define ends of tectoRNA to be addedfive_prime_980 = "GGACAG"three_prime_980 = "CUGUCC"five_prime = "CUAGGA"three_prime = "UCCUAG"ss_five = "(((((("ss_three = "))))))"###replaceall * in ss column with xs (mean variable ss allowed)#df['ss'] = df['ss'].str.replace('*','x')x = len(df)###add project name to dfproject = []for i in range(x):    project.append("28RvsGGAA_THF_TL")p = np.asarray(project)df2 = pd.DataFrame(p, columns = ['projects'])#print(df2.head())df3 = pd.concat([df2, df], axis=1, sort=False)###add THF sequence and ss ends to generated seqs and ssfor i, row in df.iterrows():    a,b,c = df['name'][i].split(":")    if '980' in c or '4LVV.4' in c:        new_seq = five_prime_980 + df['sequence'][i] + three_prime_980        df.at[i, 'sequence'] = new_seq        new_ss = ss_five + df['ss'][i] + ss_three        df.at[i, 'ss'] = new_ss    else:        new_seq = five_prime + df['sequence'][i] + three_prime        df.at[i, 'sequence'] = new_seq        new_ss = ss_five + df['ss'][i] + ss_three        df.at[i, 'ss'] = new_ss    #print(new_seq, new_ss)print(df.head())#print(df3.head())#save fiished dataframedf.to_csv("final_fngrprt_300_orig_GGAA_11ntR.csv", index=False)